public class AuthService
{
    private readonly HttpClient _httpClient;

    public async Task<bool> LoginAsync(string username, string password)
    {
        var tokenRequest = new
        {
            client_id = "maui-app",
            grant_type = "password",
            username = username,
            password = password
        };

        var response = await _httpClient
            .PostAsJsonAsync("https://keycloak.example.com/auth/realms/myrealm/protocol/openid-connect/token", 
                           tokenRequest);

        if (response.IsSuccessStatusCode)
        {
            var tokenResponse = await response.Content.ReadFromJsonAsync<TokenResponse>();

            // Store tokens securely
            await SecureStorage.SetAsync("access_token", tokenResponse.AccessToken);
            await SecureStorage.SetAsync("refresh_token", tokenResponse.RefreshToken);

            return true;
        }

        return false;
    }

    public async Task<string> GetAccessTokenAsync()
    {
        return await SecureStorage.GetAsync("access_token");
    }
}
