// composer.json example
{
    "name": "mycompany/myapp",
    "description": "My PHP Application",
    "type": "project",
    "require": {
        "php": "^8.1",
        "laravel/framework": "^10.0",
        "guzzlehttp/guzzle": "^7.0",
        "monolog/monolog": "^2.0"
    },
    "require-dev": {
        "phpunit/phpunit": "^9.0",
        "fakerphp/faker": "^1.9"
    },
    "autoload": {
        "psr-4": {
            "App\\": "app/",
            "Database\\Factories\\": "database/factories/",
            "Database\\Seeders\\": "database/seeders/"
        }
    },
    "autoload-dev": {
        "psr-4": {
            "Tests\\": "tests/"
        }
    },
    "scripts": {
        "post-autoload-dump": [
            "Illuminate\\Foundation\\ComposerScripts::postAutoloadDump",
            "@php artisan package:discover --ansi"
        ]
    },
    "config": {
        "optimize-autoloader": true,
        "preferred-install": "dist",
        "sort-packages": true
    },
    "minimum-stability": "stable",
    "prefer-stable": true
}

// Common Composer commands:
# Install dependencies
composer install

# Add a new package
composer require vendor/package

# Update packages
composer update

# Update a specific package
composer update vendor/package

# Remove a package
composer remove vendor/package

# Check for outdated packages
composer outdated

# Run scripts
composer run-script script-name

# Autoload optimization
composer dump-autoload -o

// Using Composer in code
<?php
require 'vendor/autoload.php';

// Now you can use installed packages
use Monolog\Logger;
use Monolog\Handler\StreamHandler;

// Create a log channel
$log = new Logger('name');
$log->pushHandler(new StreamHandler('path/to/your.log', Logger::WARNING));

// Add records to the log
$log->warning('Foo');
$log->error('Bar');

// Using Guzzle HTTP client
use GuzzleHttp\Client;

$client = new Client();
$response = $client->request('GET', 'https://api.github.com/user', [
    'headers' => [
        'Authorization' => 'token ' . $token,
        'User-Agent' => 'MyApp'
    ]
]);

$body = $response->getBody();
$data = json_decode($body, true);

// Variables & Output
$variable = "value";
echo "Hello World";
print "Hello";

// Data Types
$string = "text";
$integer = 42;
$float = 3.14;
$boolean = true;
$array = [1, 2, 3];
$null = null;

// Arrays
$indexed = ['a', 'b', 'c'];
$associative = ['key' => 'value'];
$multidimensional = [['a', 'b'], ['c', 'd']];

// Control Structures
if (condition) { } elseif (condition) { } else { }
switch ($var) { case 'value': break; default: }
for ($i=0; $i<10; $i++) { }
while (condition) { }
do { } while (condition);
foreach ($array as $value) { }
foreach ($array as $key => $value) { }

// Functions
function name($param) { return $value; }
fn($param) => $expression; // Arrow function

// Superglobals
$_GET, $_POST, $_REQUEST, $_SERVER
$_SESSION, $_COOKIE, $_FILES, $_ENV

// Database (PDO)
$pdo = new PDO($dsn, $user, $pass);
$stmt = $pdo->prepare("SQL");
$stmt->execute([$params]);
$result = $stmt->fetchAll();

// OOP
class ClassName {
    public $property;
    private $privateProp;

    public function method() { }
    public function __construct() { }
}

// Error Handling
try { } catch (Exception $e) { }
throw new Exception('message');

// File Operations
file_get_contents('file.txt');
file_put_contents('file.txt', $data);

// Common Functions
strlen($string), strpos($haystack, $needle)
substr($string, $start, $length)
array_push($array, $value), array_pop($array)
count($array), in_array($value, $array)
json_encode($data), json_decode($json, true)
