import { useState, useEffect } from 'react';

function UserProfile({ userId }) {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  // useEffect runs after every render
  useEffect(() => {
    // This runs after component mounts and after every update
    console.log('Component rendered');
  });

  // Empty dependency array - runs once after mount
  useEffect(() => {
    console.log('Component mounted');

    // Cleanup function (runs before unmount)
    return () => {
      console.log('Component will unmount');
    };
  }, []); // Empty array = run only on mount

  // Dependency array - runs when dependencies change
  useEffect(() => {
    if (userId) {
      setLoading(true);
      fetch(`/api/users/${userId}`)
        .then(response => response.json())
        .then(userData => {
          setUser(userData);
          setLoading(false);
        })
        .catch(error => {
          console.error('Error fetching user:', error);
          setLoading(false);
        });
    }
  }, [userId]); // Runs when userId changes

  // Cleanup for subscriptions
  useEffect(() => {
    const interval = setInterval(() => {
      console.log('Tick');
    }, 1000);

    // Cleanup function
    return () => {
      clearInterval(interval);
    };
  }, []);

  if (loading) return <div>Loading...</div>;
  if (!user) return <div>User not found</div>;

  return (
    <div>
      <h2>{user.name}</h2>
      <p>Email: {user.email}</p>
    </div>
  );
}

import { useState, useEffect } from 'react';

function DataFetching() {
  const [data, setData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Basic fetch with useEffect
  useEffect(() => {
    const fetchData = async () => {
      try {
        setLoading(true);
        const response = await fetch('https://api.example.com/data');

        if (!response.ok) {
          throw new Error('Network response was not ok');
        }

        const result = await response.json();
        setData(result);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  // POST request example
  const createItem = async (newItem) => {
    try {
      const response = await fetch('https://api.example.com/items', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(newItem),
      });

      if (!response.ok) {
        throw new Error('Failed to create item');
      }

      const createdItem = await response.json();
      setData(prev => [...prev, createdItem]);
    } catch (err) {
      setError(err.message);
    }
  };

  if (loading) return <div>Loading...</div>;
  if (error) return <div>Error: {error}</div>;

  return (
    <div>
      <h2>Data from API</h2>
      <pre>{JSON.stringify(data, null, 2)}</pre>

      <button 
        onClick={() => createItem({ name: 'New Item' })}
      >
        Add Item
      </button>
    </div>
  );
}

